import '../Register/Register.css';
import logo from '../../images/logo.svg';
import { Link } from 'react-router-dom';

function Register(props) {

  const [nameRegister, setNameRegister] = useState("");
  const [emailRegister, setEmailRegister] = useState("");
  const [passwordRegister, setPasswordRegister] = useState("");
  const [dataDirty, setDataDirty] = useState(false);
  const [dataError, setDataError] = useState(
    "Некорректный Email или пароль"
  );
  const [formValid, setFormValid] = useState(false);

  useEffect(() => {
    if (dataError) {
      setFormValid(false);
    } else {
      setFormValid(true);
    }
  }, [dataError]);

  function handleChangeName(e) {
    setNameRegister(e.target.value);
    if (
      (!e.target.validity.valid && e.target.value.length < 3) ||
      e.target.value.length > 20
    ) {
      setDataError(
        "Что-то пошло не так. Имя не должно быть пустым или некорректный Email"
      );
      if (!e.target.value) {
        setDataError("Имя не может быть пустым");
      }
    } else {
      setDataError("");
    }
  }

  function handleChangeEmail(e) {
    setEmailRegister(e.target.value);
    if (
      !String(e.target.value)
        .toLowerCase()
        .match(
          /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        )
    ) {
      setDataError(
        "Некорректное имя или Email, или неверный пароль"
      );
      if (!e.target.value) {
        setDataError("Email обязателен для заполнения");
      }
    } else {
      setDataError("");
    }
  }

  function handleChangePassword(e) {
    setPasswordRegister(e.target.value);
    if (e.target.value.length < 8 || e.target.value.length > 20) {
      setDataError(
        "Некорректный Email или неверный пароль"
      );
      if (!e.target.value) {
        setDataError("Пароль обязателен для заполнения");
      }
    } else {
      setDataError("");
    }
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!nameRegister || !emailRegister || !passwordRegister) {
      return;
    }
    props.handleRegistration(nameRegister, emailRegister, passwordRegister);
    setNameRegister("");
    setEmailRegister("");
    setPasswordRegister("");
  }

  function blurHandler(e) {
    switch (e.target.name) {
      case "name":
        setDataDirty(true);
        break;
      case "email":
        setDataDirty(true);
        break;
      case "password":
        setDataDirty(true);
        break;
      default:
        break;
    }
  }

    return (
        <div className='register'>
          <div className='register__box-header'>
            <img className='register__logo' src={logo} alt='Лоотип'/>
            <p className='register__header'>Добро пожаловать!</p>
          </div>
          <p className='register__input-header'>Имя</p>
          <input 
            className='register__input'
            id="nameregister"
            type="text"
            name="name"
            value={nameRegister}
            minLength="2"
            maxLength="40"
            onChange={handleChangeName}
            onBlur={blurHandler}
            autoComplete="on"
            required
          />
          <p className='register__input-header'>E-mail</p>
          <input 
            className='register__input' 
            type="email"
            id="emailregister"
            name="email"
            value={emailRegister}
            minLength="8"
            maxLength="40"
            onChange={handleChangeEmail}
            onBlur={blurHandler}
            autoComplete="on"
            required
          />
          <p className='register__input-header'>Пароль</p>
          <input className='register__input' type="password" />
          <button className='button register__button' type='button'>Зарегистрироваться</button>
          <p className='register__text-link'>Уже зарегистрированы?<Link to='/signin'className='link register__link'>Войти</Link></p>
        </div>
    );
  }

export default Register;
