import '../Register/Register.css';
import { useEffect, useState } from 'react';
import logo from '../../images/logo.svg';
import { Link } from 'react-router-dom';

function Register(props) {

  const [nameRegister, setNameRegister] = useState("");
  const [emailRegister, setEmailRegister] = useState("");
  const [passwordRegister, setPasswordRegister] = useState("");

  function handleNamelInput(e) {
    setNameRegister(e.target.value);
  }
  
  function handleEmailInput(e) {
    setEmailRegister(e.target.value);
  }

  function handlePasswordInput(e) {
    setPasswordRegister(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    props.handleRegistration(nameRegister, emailRegister, passwordRegister);
  }

    return (
        <div className='register'>
          <div className='register__box-header'>
            <Link  to='/'>
                <img className='button header__logo' type='button' src={logo} alt='Лоотип'/>
            </Link>
            <p className='register__header'>Добро пожаловать!</p>
          </div>
          <p className='register__input-header'>Имя</p>
          <input 
            className='register__input'
            id="nameregister"
            type="text"
            value={nameRegister}
            minLength="2"
            maxLength="40"
            onChange={handleNamelInput}
            autoComplete="on"
            required
          />
          <p className='register__input-header'>E-mail</p>
          <input 
            className='register__input' 
            type="email"
            value={emailRegister}
            minLength="8"
            maxLength="40"
            onChange={handleEmailInput}
            autoComplete="on"
            required
          />
          <p className='register__input-header'>Пароль</p>
          <input 
            className='register__input' 
            type="password"
            value={passwordRegister}
            minLength="6"
            maxLength="200"
            onChange={handlePasswordInput}
            autoComplete="on"
            required
          />
          <button 
            className='button register__button' 
            type='button' 
            onClick={handleSubmit}
          >
            Зарегистрироваться
          </button>
          <p className='register__text-link'>Уже зарегистрированы?<Link to='/signin'className='link register__link'>Войти</Link></p>
        </div>
    );
  }

export default Register;
