import '../Register/Register.css';
import { useEffect, useState } from 'react';
import logo from '../../images/logo.svg';
import { Link } from 'react-router-dom';

function Register(props) {

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nameError, setNameError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [formValid, setFormValid] = useState(false);

  function handleChangeName(e) {
    const validName = /^[a-zA-Z- ]+$/.test(e.target.value);

    if (e.target.value.length < 2) {
      setNameError("Длина имени должна быть не менее 2 символов");
    } else if (e.target.value.length > 30) {
      setNameError("Длина имени должна должна быть не более 30 символов");
    } else if (!validName) {
      setNameError("Имя должно быть указано латиницей");
    } else {
      setNameError("");
    }
    setName(e.target.value);
  }

  function handleChangeEmail(e) {
    const validEmail = /^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i.test(
      e.target.value
    );

    if (!validEmail) {
      setEmailError("Неверный формат почты");
    } else {
      setEmailError("");
    }
    setEmail(e.target.value);
  }

  function handleChangePassword(e) {
    if (e.target.value.length < 6) {
      setPasswordError("Пароль должен быть не менее 6 символов");
    } else {
      setPasswordError("");
    }
    setPassword(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    props.onRegister(name, email, password);
  }

  useEffect(() => {
    if (
      name &&
      email &&
      password &&
      !nameError &&
      !emailError &&
      !passwordError
    ) {
      setFormValid(true);
    } else {
      setFormValid(false);
    }
  }, [name, email, password, nameError, emailError, passwordError]);


  // function handleNamelInput(e) {
  //   setNameRegister(e.target.value);
  // }
  
  // function handleEmailInput(e) {
  //   setEmailRegister(e.target.value);
  // }

  // function handlePasswordInput(e) {
  //   setPasswordRegister(e.target.value);
  // }

  // function handleSubmit(e) {
  //   e.preventDefault();
  //   props.handleRegistration(nameRegister, emailRegister, passwordRegister);
  // }

  

    return (
        <div className='register'>
          <div className='register__box-header'>
            <Link  to='/'>
                <img className='button header__logo' type='button' src={logo} alt='Лоотип'/>
            </Link>
            <p className='register__header'>Добро пожаловать!</p>
          </div>
          <p className='register__input-header'>Имя</p>
          <input 
            className='register__input'
            id="nameregister"
            type="text"
            value={nameRegister}
            minLength="2"
            maxLength="40"
            onChange={handleNamelInput}
            autoComplete="on"
            required
          />
          <span className="form__span-error">
            {nameError}
          </span>
          <p className='register__input-header'>E-mail</p>
          <input 
            className='register__input' 
            type="email"
            value={emailRegister}
            minLength="8"
            maxLength="40"
            onChange={handleEmailInput}
            autoComplete="on"
            required
          />
          <span className="form__span-error">
            {emailError}
          </span>
          <p className='register__input-header'>Пароль</p>
          <input 
            className='register__input' 
            type="password"
            value={passwordRegister}
            minLength="6"
            maxLength="200"
            onChange={handlePasswordInput}
            autoComplete="on"
            required
          />
          <span className="form__span-error">
            {passwordError}
          </span>
          <button 
            className='button register__button' 
            type="submit"
            onClick={handleSubmit}
            disabled={!formValid}
          >
            Зарегистрироваться
          </button>
          <p className='register__text-link'>Уже зарегистрированы?<Link to='/signin'className='link register__link'>Войти</Link></p>
        </div>
    );
  }

export default Register;
