import React from "react";
import { Link } from "react-router-dom";
import logo from '../../images/logo.svg';
import "../formAndLoginForm/formAndLoginForm.css";

function formAndLoginForm(props) {
  return (
    <div className='form'>
        <div  className='form__box-header'>
            <Link  to='/'>
                    <img className='button header__logo' type='button' src={logo} alt='Лоотип'/>
            </Link>
            <p className='form__header'>{props.title}</p>
        </div>
        {props.children}
        <button
            type="submit"
            className='button form__button'
        >
            {buttonText}
        </button>
        <p className='form__text-link'>Уже зарегистрированы?<Link to='/signin'className='link form__link'>Войти</Link></p>
    </div>
  );
}

export default formAndLoginForm;

