import './MoviesCard.css';

function MoviesCard(props) {
    return (
        <div className='cardMovie__card'>
            <div className='cardMovie__box-heading'>
                <p className='cardMovie__heading'>{props.nameRU}</p>
                <button className='button cardMovie__button-favourites cardMovie__button-favourites-active' />
                <p className='cardMovie__duration'>{props.duration}</p>
            </div>
            <img className='cardMovie__card-img' alt={props.nameRU}  src={`https://api.nomoreparties.co/${props.image.url}`} />
        </div>  
    );
}

export default MoviesCard;
